    .replace(/<style[^>]*>[\s\S]*?<\/style>/gi, "")
    .replace(/<!--[\s\S]*?-->/g, "");

  // 2. Eliminar etiquetas HTML
  let cleanText = htmlContent.replace(/<[^>]+>/g, " ");

  // 3. Eliminar texto basura com√∫n
  const palabrasBasura = [
    "Men√∫ principal", "Navegaci√≥n", "Buscar", "Donaciones", "Acceder",
    "Herramientas personales", "P√°ginas especiales", "Contribuciones",
    "Discusi√≥n", "P√°gina aleatoria", "M√°s informaci√≥n"
  ];
  palabrasBasura.forEach(palabra => {
    cleanText = cleanText.replace(new RegExp(`\\b${palabra}\\b`, "gi"), "");
  });

  // 4. Normalizar espacios en blanco y limpiar texto
  cleanText = cleanText.replace(/\s+/g, " ").trim();

  // 5. Resumir a 200 palabras
  const summary = cleanText.split(" ").slice(0, 200).join(" ");

  return {
    id: `${index + 1}`,
